    height: 400px;
}
.item {
    position: relative;
    height: 400px;
    width: 200px;
    border: 1px solid red //为了标出白色的图形，加了一个红的边，之后会删掉
}
.white {
    background: rgba(255, 255, 255, 1);
}
.black {
